import React, { useState } from "react";
import axios from "axios";

const StudentForm = () => {
  const [student, setStudent] = useState({
    name: "",
    email: "",
    age: "",
    course: "",
  });

  const [errors, setErrors] = useState({});

  // Input Change Handler
  const handleChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  // Form Validation
  const validateForm = () => {
    let errors = {};
    if (!student.name) errors.name = "Name is required";
    if (!student.email) {
      errors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(student.email)) {
      errors.email = "Invalid email format";
    }
    if (!student.age) errors.age = "Age is required";
    if (!student.course) errors.course = "Course is required";
    return errors;
  };

  // Form Submit Handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/students", student);
      alert("Student registered successfully!");
      setStudent({ name: "", email: "", age: "", course: "" });
      setErrors({});
    } catch (error) {
      console.error("Error registering student:", error);
      alert("Failed to register student");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="border p-4 shadow-sm rounded">
      <div className="mb-3">
        <label className="form-label">Name</label>
        <input
          type="text"
          name="name"
          className="form-control"
          value={student.name}
          onChange={handleChange}
        />
        {errors.name && <small className="text-danger">{errors.name}</small>}
      </div>

      <div className="mb-3">
        <label className="form-label">Email</label>
        <input
          type="email"
          name="email"
          className="form-control"
          value={student.email}
          onChange={handleChange}
        />
        {errors.email && <small className="text-danger">{errors.email}</small>}
      </div>

      <div className="mb-3">
        <label className="form-label">Age</label>
        <input
          type="number"
          name="age"
          className="form-control"
          value={student.age}
          onChange={handleChange}
        />
        {errors.age && <small className="text-danger">{errors.age}</small>}
      </div>

      <div className="mb-3">
        <label className="form-label">Course</label>
        <input
          type="text"
          name="course"
          className="form-control"
          value={student.course}
          onChange={handleChange}
        />
        {errors.course && <small className="text-danger">{errors.course}</small>}
      </div>

      <button type="submit" className="btn btn-primary">Register</button>
    </form>
  );
};

export default StudentForm;
